package vn.edu.iuh.fit.model;import jakarta.persistence.*;import lombok.*;import org.springframework.data.redis.core.RedisHash;import java.io.Serializable;@Data@Entity@Table(name="book")@NoArgsConstructor@AllArgsConstructor@Getter @Setter@RedisHash("Book")public class Book implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private long id;    private String bookName;    private String title;    private String author;    private String publisher;    @Override    public String toString() {        return "Book{" +                "id=" + id +                ", bookName='" + bookName + '\'' +                ", title='" + title + '\'' +                ", author='" + author + '\'' +                ", publisher='" + publisher + '\'' +                '}';    }}